				on(backhead.domNode, "span:click", function(evt){
                                        console.log("Click: ", evt);
                                        var rel = domAttr.get(evt.target,"rel");
                                        switch(rel){
                                                case "flip":
                                                        _self.dialog.flip();
                                                        break;
                                        }
                                });	
